INSERT INTO `%%PRFX%%_modul_sysblock` VALUES ('5', 'Текущая дата', '<?php\r  switch (date(\"F\")){\r    case ''January'':   $month = ''января'';   break;\r    case ''February'':  $month = ''февраля'';  break;\r    case ''March'':     $month = ''марта'';    break;\r    case ''April'':     $month = ''апреля'';   break;\r    case ''May'':       $month = ''мая'';      break;\r    case ''June'':      $month = ''июня'';     break;\r    case ''July'':      $month = ''июля'';     break;\r    case ''August'':    $month = ''августа'';  break;\r    case ''September'': $month = ''сентября''; break;\r    case ''October'':   $month = ''октября'';  break;\r    case ''November'':  $month = ''ноября'';   break;\r    case ''December'':  $month = ''декабря'';  break;\r  }\r\r  switch (date(\"w\")){\r    case ''0'': $day = ''Воскресенье''; break;\r    case ''1'': $day = ''Понедельник''; break;\r    case ''2'': $day = ''Вторник''; break;\r    case ''3'': $day = ''Среда''; break;\r    case ''4'': $day = ''Четверг''; break;\r    case ''5'': $day = ''Пятница''; break;\r    case ''6'': $day = ''Суббота''; break;\r  }\r\r  $now_date = date(\"$day, d \".$month.\" Y г.\");\r  echo ''Сегодня: '' . $now_date;\r?>');#inst#
