INSERT INTO `%%PRFX%%_modul_sysblock` VALUES ('7', 'Последние документы', '<div class=\"mod_searchbox\"><strong>Последние документы</strong><br />\r<br />\r<?php\r  $limit = 5; // Количество документов в списке\r  $sql = $GLOBALS[''db'']->Query(\"SELECT * \r    FROM \" . PREFIX . \"_documents\r    WHERE Id != 1\r      AND Id != 2\r      AND Geloescht != 1\r      AND DokStatus != 0\r      AND (DokEnde = 0 || DokEnde > \".time().\")\r      AND (DokStart = 0 || DokStart < \".time().\")\r    ORDER BY DokStart DESC\r    LIMIT 0,$limit\r  \");\r  $outstring = '''';\r  $i = 0;\r  while ($row = $sql->fetchrow()) {\r    $Url = (CP_REWRITE==1) ? cp_rewrite(''index.php?id=''.$row->Id.''&amp;doc=''.cp_parse_linkname($row->Titel)) : ''index.php?id=''.$row->Id.''&amp;doc=''.cp_parse_linkname($row->Titel);\r    $outstring .= ''<strong>''.++$i.''.</strong> '';\r    $outstring .= ''<a href=\"''.$Url.''\">''.substr($row->Titel, 0, 40).''''.((strlen($row->Titel)>40) ? ''...'' : '''').''</a><br />'';\r  }\r  $sql->Close();\r  echo $outstring;\r?></div>');#inst#
INSERT INTO `%%PRFX%%_modul_sysblock` VALUES ('8', 'Форум: Популярные темы', '<div class=\"mod_searchbox\"><strong>Популярные темы</strong><br />\r<br />\r<ol> <?php\r  $limit = 5;\r  $sql = $GLOBALS[''db'']->Query(\"SELECT topic.id,forum_id,title,views,replies,datum,BenutzerId,BenutzerName\r    FROM \" . PREFIX . \"_modul_forum_topic AS topic\r    LEFT JOIN \" . PREFIX . \"_modul_forum_userprofile AS user ON user.BenutzerId = uid\r    WHERE opened = 1\r    ORDER BY views DESC\r    LIMIT 0,$limit\r  \");\r  $outstring = '''';\r  while ($row = $sql->FetchRow()) {\r    $outstring .= ''<li>'';\r    $outstring .= ''<strong><a href=\"index.php?module=forums&show=showtopic&toid=''.$row->id.''&fid=''.$row->forum_id.''\">''.htmlentities(stripslashes($row->title), ENT_NOQUOTES, \"cp1251\").''</a></strong><br />'';\r    $outstring .= ''Тема cоздана ''.date_format(date_create($row->datum),\"d-m-Y г. в H:i\");\r    $outstring .= '' пользователем <a href=\"index.php?module=forums&show=userprofile&user_id=''.$row->BenutzerId.''\" class=\"name\">''.htmlentities(stripslashes($row->BenutzerName), ENT_NOQUOTES, \"cp1251\").''</a><br />'';\r    $outstring .= '' (просмотров: ''.$row->views;\r    $outstring .= '', сообщений: ''.$row->replies.'')'';\r    $outstring .= ''</li>'';\r  }\r  $sql->Close();\r  echo $outstring;\r?> </ol>\r    </div>');#inst#
INSERT INTO `%%PRFX%%_modul_sysblock` VALUES ('9', 'Форум: Важные темы и объявления', '<div class=\"mod_searchbox\"><strong>Важные темы и объявления</strong><br />\r<br />\r<ol> <?php\r// Для вывода только важных тем ипользовать WHERE type = 1\r// Для вывода только объявлений ипользовать WHERE type = 100\r// Для вывода важных тем и объявлений ипользовать WHERE type != 0\r  $limit = 10;\r  $sql = $GLOBALS[''db'']->Query(\"SELECT topic.id,forum_id,title,views,replies,datum,BenutzerId,BenutzerName\r    FROM \" . PREFIX . \"_modul_forum_topic AS topic\r    LEFT JOIN \" . PREFIX . \"_modul_forum_userprofile AS user ON user.BenutzerId = uid\r    WHERE type != 0\r    ORDER BY views DESC\r    LIMIT 0,$limit\r  \");\r  $outstring = '''';\r  while ($row = $sql->FetchRow()) {\r    $outstring .= ''<li>'';\r    $outstring .= ''<strong><a href=\"index.php?module=forums&show=showtopic&toid=''.$row->id.''&fid=''.$row->forum_id.''\">''.htmlentities(stripslashes($row->title), ENT_NOQUOTES, \"cp1251\").''</a></strong><br />'';\r    $outstring .= ''Тема cоздана ''.date_format(date_create($row->datum),\"d-m-Y г. в H:i\");\r    $outstring .= '' пользователем <a href=\"index.php?module=forums&show=userprofile&user_id=''.$row->BenutzerId.''\" class=\"name\">''.htmlentities(stripslashes($row->BenutzerName), ENT_NOQUOTES, \"cp1251\").''</a><br />'';\r    $outstring .= '' (просмотров: ''.$row->views;\r    $outstring .= '', сообщений: ''.$row->replies.'')'';\r    $outstring .= ''</li>'';\r  }\r  $sql->Close();\r  echo $outstring;\r?> </ol>\r    </div>');#inst#
