INSERT INTO `%%PRFX%%_modul_sysblock` VALUES ('13', 'Объединённые блоки 1, 2 и 3 для левой панели', '<div class="searchbox">\r\n<h3>Популярные новости</h3>\r\n<?php\r\n$limit = 3; // Количество новостей в списке\r\n$i = 0;\r\n$sql = $GLOBALS[''db'']->Query("SELECT a.DokId, COUNT(a.DokId) AS top_comments, b.Id, b.Titel, b.Url \r\n    FROM " . PREFIX . "_modul_comment_info AS a, " . PREFIX . "_documents AS b\r\n    WHERE a.DokId = b.Id \r\n    GROUP BY (a.DokId) \r\n    ORDER BY top_comments DESC \r\n    LIMIT " . $limit\r\n);\r\nwhile ($row = $sql->fetchrow()){\r\n	$Url = ''index.php?id='' . $row->Id . ''&am'' . ''p;doc='' . (empty($row->Url) ? cp_parse_linkname(stripslashes($row->Titel)) : $row->Url);\r\n	echo ''<strong>'', ++$i, ''.</strong> '',\r\n	''<a href="'', (CP_REWRITE == 1) ? cp_rewrite($Url) : $Url, ''">'', substr($row->Titel, 0, 36), ((strlen($row->Titel)>36) ? ''...'' : ''''), ''</a>'',\r\n	''<p style="text-align:right;">(Комментариев: '', $row->top_comments, '')</p>'';\r\n}\r\n?></div>\r\n<div class="searchbox">\r\n<h3>Активные комментаторы</h3>\r\n<?php\r\n$limit = 5;\r\n$i = 0;\r\n$sql = $GLOBALS[''db'']->Query("SELECT Author, COUNT(Author_Id) as top_comments\r\n    FROM " . PREFIX . "_modul_comment_info\r\n    WHERE Author_Id != ''0''\r\n    GROUP BY (Author)\r\n    ORDER BY top_comments DESC\r\n    LIMIT " . $limit\r\n);\r\nwhile ($row = $sql->fetchrow()){\r\n	echo ''<strong>'', ++$i, '' место:</strong> '', $row->Author, \r\n		''<br /><div style="text-align:right; font-size:11px;">(Комментариев: '', $row->top_comments, '')</div>'';\r\n}\r\n?></div>\r\n<div class="searchbox">\r\n<h3>Новые комментарии:</h3>\r\n<?php\r\n$limit = 3;\r\n$sql = $GLOBALS[''db'']->Query("SELECT a.Id, a.Author, a.DokId, a.Text, a.Erstellt, b.Id, b.Titel, b.Url \r\n    FROM " . PREFIX . "_modul_comment_info AS a \r\n    LEFT JOIN " . PREFIX . "_documents AS b ON a.DokId = b.Id\r\n    WHERE Status = 1\r\n    ORDER BY a.Id DESC \r\n    LIMIT " . $limit\r\n);\r\nwhile ($row = $sql->fetchrow()){\r\n	$Url = ''index.php?id='' . $row->Id . ''&am'' . ''p;doc='' . (empty($row->Url) ? cp_parse_linkname(stripslashes($row->Titel)) : $row->Url);\r\n	echo ''<small>Ответил:'', $row->Author,\r\n	''<p>"...<em>'', substr($row->Text, 0, 150), ''</em>"</p>'',\r\n	''<small>'', date("d-m-Y г. H:i", $row->Erstellt), '' | <a href="'', (CP_REWRITE == 1) ? cp_rewrite($Url) : $Url, ''">Подробнее...</a></small>'',\r\n	''</small><hr/>'';\r\n}\r\n?></div>');#inst#
