INSERT INTO `%%PRFX%%_modul_sysblock` VALUES ('2', 'Последние комментарии', '<div class=\"searchbox\"><strong>Новые комментарии:</strong>\r\n \r\n <?php\r\n  $limit = 3;\r\n  $sql = $GLOBALS[''db'']->Query(\"SELECT a.Id, a.Author, a.DokId, a.Text, a.Erstellt, b.Id, b.Titel FROM\r\n  \" . PREFIX . \"_modul_comment_info as a LEFT JOIN\r\n  \" . PREFIX . \"_documents as b ON a.DokId = b.Id\r\n  WHERE Status = 1\r\n  ORDER BY\r\n  a.Id DESC LIMIT 0,$limit\");\r\n\r\n  $outstring = ''''; \r\n  while ($row = $sql->fetchrow()){\r\n    $Url = (CP_REWRITE==1) ? cp_rewrite(''index.php?id=''.$row->Id.''&doc=''.cp_parse_linkname($row->Titel)) : ''index.php?id=''.$row->Id.''&doc=''.cp_parse_linkname($row->Titel);\r\n    $outstring .= ''<small>Ответил:''.$row->Author;\r\n    $outstring .= ''<p>\"...<em>''.substr($row->Text, 0, 150).''</em>\"</p>'';\r\n    $outstring .= ''<small>''.date(\"d-m-Y г. H:i\",$row->Erstellt).'' | <a href=\"''.$Url.''\">Подробнее...</a></small>'';\r\n    $outstring .= ''</small><hr/>'';\r\n  }\r\n  echo $outstring;\r\n\r\n  $sql->Close();\r\n?></div>');#inst#
INSERT INTO `%%PRFX%%_modul_sysblock` VALUES ('3', 'Популярные новости', '<div class=\"searchbox\">\r\n<h3>Популярные новости</h3>\r\n\r\n<?php\r\n  $limit = 3; // Количество новостей в списке\r\n  $sql = $GLOBALS[''db'']->Query(\"SELECT \r\n      a.DokId, \r\n      COUNT(a.DokId) as top_comments, \r\n      b.Id as did, \r\n      b.Titel as dt \r\n    FROM \r\n      \" . PREFIX . \"_modul_comment_info as a, \r\n      \" . PREFIX . \"_documents as b\r\n    WHERE \r\n      a.DokId = b.Id \r\n    GROUP BY (a.DokId) \r\n    ORDER BY top_comments DESC \r\n    LIMIT 0,$limit\r\n  \");\r\n  $outstring = '''';\r\n  while ($row = $sql->fetchrow()){\r\n    $Url = (CP_REWRITE==1) ? cp_rewrite(''index.php?id=''.$row->did.''&doc=''.cp_parse_linkname($row->dt)) : ''index.php?id=''.$row->did.''&doc=''.cp_parse_linkname($row->dt);\r\n    $outstring .= ''<strong>''.++$i.''.</strong> ''; \r\n    $outstring .= ''<a href=\"''.$Url.''\">''.substr($row->dt, 0, 36).((strlen($row->dt)>36) ? ''...'' : '''').''</a>'';\r\n    $outstring .= ''<p style=\"text-align:right;\">(Комментариев: ''.$row->top_comments.'')</p>'';\r\n  }\r\n  echo $outstring;\r\n\r\n  $sql->Close();\r\n?>\r\n</div>');#inst#
